import React from 'react';


function Message({props}) {
  const [prop,setProp] = props
  
  useEffect(() => {
    const timerId = setTimeout(() => {
        setProp('');
    }, 5000);

    return () => clearTimeout(timerId);
  }, [prop]);

  return (
    <>
      {prop && <p className="message">{prop}</p>}
    </>
  );
}

export default Message;
