import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

import  './login.css';

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async () => {
    //event.preventDefault();

    try {
      const userDetails = { 'roleId' : 1,'userName' : username, 'password' : password };
      const response = await axios.post("http://localhost:8044/getAllUserDetails");
      // localStorage.setItem("token", response.data.token); // Store token locally (replace with secure storage)
      const status = response.status;
      navigate("/home");
    } catch (error) {
      setError(error.response.data.message || "Login failed");
    }
  };
  const handleRegisterClick = () => {
     // Get navigate function within component
    navigate("/register");
  };
  
// };
return (
  <div className="login-container"> 
    
    <form onSubmit={handleSubmit} className="form"> 
    <h2 className="head">Login</h2>
      <label htmlFor="username" className="label">Username:</label>
      <input
        type="text"
        id="username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
        className="input" 
     />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        className="input" 
      />
      <button type="submit" className="button">Log In</button>
      {error && <p className="error">{error}</p>}
    </form>
    <p className="para">Please register if you are not registered</p>
    <button className="register-button" onClick={handleRegisterClick}>
        Register
      </button>
  </div>
);
};

export default LoginPage;
