import {React,useEffect} from 'react';


function Message({props,name}) {
  const [prop,setProp] = props
  
  useEffect(() => {
    const timerId = setTimeout(() => {
        setProp({ ...prop, [name]: '' });
    }, 5000);

    return () => clearTimeout(timerId);
  }, [name]);

  return (
    <>
      {prop.name && <p className="message">{prop.name}</p>}
    </>
  );
}

export default Message;
