create table if not exists REP_USER_LOGIN(USER_ID INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,ROLE_ID int,USERNAME varchar(20),PASSWORD varchar(20),FAILEDATTEMPTS int);
create table if not exists REP_METADATA_QUERIES(QUERY_ID INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,METADATA_NAME nvarchar(50),METADATA_QUERY nvarchar(255));
create table if not exists REP_PRODUCT_DETAILS(PRODUCT_ID INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,PRODUCT_NAME varchar(50),BRAND_NAME varchar(50),SUPPLIER_NAME varchar(50),PRODUCT_COST DECIMAL(38,2),CATEGORY VARCHAR(50),SUBCATEGORY VARCHAR(50));

