insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('saveUserDetails','insert into REP_USER_LOGIN (ROLE_ID,USERNAME,PASSWORD,FAILEDATTEMPTS) values(:roleId,:userName,:password,0)');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('isExistsUser','select USERNAME from REP_USER_LOGIN where USERNAME = ?');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('validateLogin','select USERNAME from REP_USER_LOGIN where USERNAME = ? and PASSWORD = ?');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('getFailedAttempts','select FAILEDATTEMPTS from REP_USER_LOGIN where USERNAME = ?');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('updateFailedAttempts','update REP_USER_LOGIN set FAILEDATTEMPTS = FAILEDATTEMPTS + 1 where USERNAME = ?');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('deleteUserDetails','delete from REP_USER_LOGIN where USERNAME = ?');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('getAllUserDetails','select * from REP_USER_LOGIN');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('saveProductDetails','insert into REP_PRODUCT_DETAILS (PRODUCT_NAME,BRAND_NAME,SUPPLIER_NAME,PRODUCT_COST,CATEGORY,SUBCATEGORY) values(:productName,:brandName,:supplierName,:productCost,:category,:subCategory)');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('getAllProductCategory','select CATEGORY from REP_PRODUCT_DETAILS');
insert into REP_METADATA_QUERIES(METADATA_NAME,METADATA_QUERY) VALUES('getAllProductDetails','select * from REP_PRODUCT_DETAILS');
